      [189,"Sa puntra morto si e ke respòns òf si e ke santana òf si e ke bela?","uitdrukking","n.v.t.",'','',"Kontesta di un persona ku no por nenga loke e tin masha gana di haña na e momento ei",'','','',''],
      [190,"Sarna ku gustu no ta pika","uitdrukking","n.v.t.","Sarna ku gustu no ta pika i si e pika e no ta mortifiká ",'',"Pa indiká ku ora un hende emprendé algu, konsiente i di su propio gustu, ni maske e topa ku kontratempu, e no ta deskurashá.",'','','',''],
