      [224,"Trifulka","sustantivo","nee",'','',"(sef.) Bruandashi, diskushon, pleitu, babel. Trastorno i konfushon entre vários persona",'','','',''],
      [225,"Un kayente, un friu","uitdrukking","n.v.t.",'','',"Elogio i alabes reproche",'','','',''],
      [226,"Un skat di kos ","uitdrukking","n.v.t.",'','',"Un tou di kos"," un kantidat di kos",'','',''],
      [227,"Un pinga","sustantivo","n.v.t.",'','',"Un gota; Tambe: djis un ratu, masha tiki ratu; falta un pinga pa… para un pinga pa… ta hera un pinga pa…// Si no yobe lo pinga. Haña un parti òf algu di tur manera, sea hopi òf tiki",'','','',''],
      [228,"Wabi","sustantivo","ja",'','',"Outo bieu"," tambe usá ora bo ke bisa ku bo no ta sinti bo kurpa shen porshento. Awe e kurpa ta wabi"," Tambe: Awe e kurpa ta wadjo.",'',''],
      [229,"Wadjo","athetivo","n.v.t.",'','',"Falsu; Bisá di muzik; Flùit wadjo; kanta wadjo; Tambe pa deskribí situashon di salú: Awe mi no ta sinti muchu bon, e kurpa ta wadjo; tambe: E kurpa ta wabi.",'','','',''],
      [230,"Waiki","sustantivo","ja",'','',"Paña chikí; ferbant di luna estilo bieu, esta pida paña ku hende muhé tabata usa (re-usabel) ántes ora nan ta na regla",'','','',''],
