    # ["nummer","lemma","woordsoort (uitdrukking, of 1 van de woordsoorten)","telbaar (ja, nee, n.v.t)","Variatie(s)","Synoniem(en)","beschrijving",'','','',''],
    # {0=>"nummer", 1=>"lemma", 2=>"woordsoort (uitdrukking, of 1 van de woordsoorten)",
    #  3=>"telbaar (ja, nee, n.v.t)", 4=>"Variatie(s)", 5=>"Synoniem(en)", 6=>"beschrijving",
    #  7=>"", 8=>"", 9=>"", 10=>""} (toevoegen aan beschrijving)
    domperere.each do |domp|
      word = Word.create(name: domp[1], sources: [source])
      word.uitdrukking = 2 if domp[2] == "uitdrukking"
      wordtype = Wordtype.find_by_name(domp[2])
      word.wordtypes=[wordtype] if wordtype
      word.countable = 1 if domp[3].downcase == 'ja'
      word.synonym = [domp[4], domp[5]].join('; ') unless domp[4].blank? && domp[5].blank?
      word.description_pap_cw = domp[6..10].join "; "
      word.user_id = this_user.id
      word.save
    end
  end

  def down
    remove_column :words, :uitdrukking
    source = Source.find_by_name('Dòmpereré pa palabra pèrdí i nobo')
    source.words.destroy_all
    source.destroy
  end
end
